// Appends URL to the copied material
function addLink(){var a=document.getElementsByTagName("body")[0];var b;b=window.getSelection();var c="<br /> URL: <a href='"+document.location.href+"'>"+document.location.href+"</a>";var e=b+c;var d=document.createElement("section");d.style.position="absolute";d.style.left="-99999px";a.appendChild(d);d.innerHTML=e;b.selectAllChildren(d);window.setTimeout(function(){a.removeChild(d)},0)}document.oncopy=addLink;